$_MODULE['<{hipay_enterprise}prestashop>faq_a71de8cd025fbee38ce0149befab444d'] = 'To check if an error occurred during the notification, check the hipay-error and hipay-callback logs.';
$_MODULE['<{hipay_enterprise}prestashop>faq_75eccb1519d39057f6921092e3fb01a3'] = 'What to do when payment errors occur ?';
$_MODULE['<{hipay_enterprise}prestashop>faq_0c97213a29d4b9b2203ece94f32535fe'] = 'Make sure that your credentials are correctly set and that the module is in the mode you want (Test or Production).';
$_MODULE['<{hipay_enterprise}prestashop>faq_a5324105726e8f3993907593c7a4ba80'] = 'Check that the related payment methods are activated in your contract(s).';
$_MODULE['<{hipay_enterprise}prestashop>faq_8bbd45d3a4db52311fcb80fc85fd3255'] = 'Check the version of the installed module, and upgrade the module if the version is old.';
$_MODULE['<{hipay_enterprise}prestashop>faq_fc3c11e9e054174433ea9220da09e367'] = 'Check HiPay logs to see if any errors appear. Then send these logs to the HiPay Support team.';
$_MODULE['<{hipay_enterprise}prestashop>faq_42e20a08e9c4316b9c2ab43597122acd'] = 'Check that your servers are not behind a proxy. If so, provide the proxy information in the module configuration.';
$_MODULE['<{hipay_enterprise}prestashop>faq_4f99b8f85fefb8daabd91436516d205c'] = 'How come my payment method(s) do(es) not appear in the order funnel ?';
$_MODULE['<{hipay_enterprise}prestashop>faq_e979014b6180681735b8e70a420c6de7'] = 'Check that the HiPay Enterprise module is properly set up with your currencies and your carriers in the “Improve > Payment > Preference” menu. When adding a carrier or a currency, you should always activate them in this setup screen.';
$_MODULE['<{hipay_enterprise}prestashop>faq_f6b10c99c0bd41e8258a8972fa40830d'] = 'Check in the HiPay module configuration that the payment method(s) is/are enabled for test countries and currencies.';
